export default class Ball{constructor(i,t){this.image=document.querySelector("#imgBall"),this.position={x:10,y:10},this.Speed={x:2,y:2},this.Size=16,this.GameWidth=i,this.GameHeight=t}draw(i){i.drawImage(this.image,this.position.x,this.position.y,this.Size,this.Size)}update(i){this.position.x+=this.Speed.x,this.position.y+=this.Speed.y}}