export default class InputHandler{constructor(e){document.addEventListener("keydown",s=>{switch(s.keyCode){case 37:case 65:e.moveLeft();break;case 39:case 68:e.moveRight()}}),document.addEventListener("keyup",s=>{switch(s.keyCode){case 37:case 65:e.Speed<0&&e.stop();break;case 39:case 68:e.Speed>0&&e.stop()}})}}